 46â†’		selectedPrefix: (str: string) => `\x1b[7m${str}\x1b[0m`,
 47â†’		selectedText: (str: string) => `\x1b[7m${str}\x1b[0m`,
 48â†’		description: (str: string) => `\x1b[2m${str}\x1b[0m`,
 49â†’		scrollInfo: (str: string) => `\x1b[2m${str}\x1b[0m`,
 50â†’		noMatch: (str: string) => `\x1b[2m${str}\x1b[0m`,
 51â†’	},
 52â†’};
 53â†’
 54â†’interface ToolCallStatus {
 55â†’	toolName: string;
 56â†’	args: Record<string, unknown>;
 57â†’	done: boolean;
 58â†’}
 59â†’
 60â†’function formatToolArgs(args: Record<string, unknown>): string {
 61â†’	const parts: string[] = [];
 62â†’	for (const [key, value] of Object.entries(args)) {
 63â†’		if (typeof value === "string") {
 64â†’			if (key === "path" || key === "file" || key === "filePath") {
 65â†’				const basename = value.split("/").pop() ?? value;
 66â†’				parts.push(`"${basename}"`);
 67â†’			} else if (value.length > 30) {
 68â†’				parts.push(`"${value.slice(0, 27)}..."`);
 69â†’			} else {
 70â†’				parts.push(`"${value}"`);
 71â†’			}
 72â†’		}
 73â†’	}
 74â†’	return parts.join(", ");
 75â†’}
 76â†’
 77â†’class Header implements Component {
 78â†’	private sessionId: string;
 79â†’	private model: string;
 80â†’	private provider: string;
 81â†’
 82â†’	constructor(options: TuiOptions) {
 83â†’		this.sessionId = options.sessionId;
 84â†’		this.model = options.model;
 85â†’		this.provider = options.provider;
 86â†’	}
 87â†’
 88â†’	invalidate(): void {}
 89â†’
 90â†’	render(width: number): string[] {
 91â†’		const sessionPart = `Session: ${this.sessionId}`;
 92â†’		const modelPart = `${this.provider}/${this.model}`;
 93â†’
 94â†’		const content = `${sessionPart} â”‚ ${modelPart}`;
 95â†’
 96â†’		const line = content.length > width ? `${content.slice(0, width - 3)}...` : content;
 97â†’		return [`\x1b[1;36m${line}\x1b[0m`];
 98â†’	}
 99â†’}
100â†’
101â†’class MessageArea implements Component {
102â†’	private messages: MessageItem[] = [];
103â†’	private ui: TUI;
104â†’	private thinking = false;
105â†’	private frame = 0;
106â†’	private animationInterval: ReturnType<typeof setInterval> | null = null;
107â†’	private reasoningText = "";
108â†’	private activeToolCalls: ToolCallStatus[] = [];
109â†’
110â†’	private static readonly THINKING_FRAMES = ["Ê•â€¢á´¥â€¢Ê”:", "Ê•Â·á´¥Â·Ê”:", "Ê•Ë˜á´¥Ë˜Ê”:", "Ê•-á´¥-Ê”:"];
111â†’	private static readonly THINKING_INTERVAL_MS = 800;
112â†’
113â†’	constructor(ui: TUI) {
114â†’		this.ui = ui;
115â†’	}
116â†’
117â†’	addMessage(message: MessageItem): void {
118â†’		this.messages.push(message);
119â†’	}
120â†’
121â†’	clear(): void {
122â†’		this.messages = [];
123â†’	}
124â†’
125â†’	startThinking(): void {
126â†’		this.thinking = true;
127â†’		this.frame = 0;
128â†’		this.animationInterval = setInterval(() => {
129â†’			this.frame++;
130â†’			this.ui.requestRender();
131â†’		}, MessageArea.THINKING_INTERVAL_MS);
132â†’	}
133â†’
134â†’	stopThinking(): void {
135â†’		this.thinking = false;
136â†’		if (this.animationInterval) {
137â†’			clearInterval(this.animationInterval);
138â†’			this.animationInterval = null;
139â†’		}
140â†’	}
141â†’
142â†’	setReasoning(text: string): void {
143â†’		this.reasoningText += text;
144â†’		const lines = this.reasoningText.split("\n");
145â†’		this.reasoningText = lines[lines.length - 1];
146â†’	}
147â†’
148â†’	clearReasoning(): void {
149â†’		this.reasoningText = "";
150â†’	}
151â†’
152â†’	addToolCall(toolName: string, args: Record<string, unknown>): void {
153â†’		this.activeToolCalls.push({ toolName, args, done: false });
154â†’	}
155â†’
156â†’	completeToolCall(toolName: string): void {
157â†’		for (let i = this.activeToolCalls.length - 1; i >= 0; i--) {
158â†’			if (this.activeToolCalls[i].toolName === toolName && !this.activeToolCalls[i].done) {
159â†’				this.activeToolCalls[i].done = true;
160â†’				break;
161â†’			}
162â†’		}
163â†’	}
164â†’
165â†’	clearToolCalls(): void {
166â†’		this.activeToolCalls = [];
167â†’	}
168â†’
169â†’	private streaming = false;
170â†’
171â†’	startStreamingMessage(): void {
172â†’		this.messages.push({ role: "assistant", content: "" });
173â†’		this.streaming = true;
174â†’	}
175â†’
176â†’	appendToStream(chunk: string): void {
177â†’		if (!this.streaming || this.messages.length === 0) return;
178â†’		const last = this.messages[this.messages.length - 1];
179â†’		last.content += chunk;
180â†’	}
181â†’
182â†’	finishStreaming(): void {
183â†’		this.streaming = false;
184â†’	}
185â†’
186â†’	invalidate(): void {}
187â†’
188â†’	private renderToolCallLines(): string[] {
189â†’		const lines: string[] = [];
190â†’		for (const tc of this.activeToolCalls) {
191â†’			const icon = tc.done ? "âœ“" : "âŸ³";
192â†’			const style = tc.done ? "\x1b[2m" : "\x1b[33m";
193â†’			const argStr = formatToolArgs(tc.args);
194â†’			lines.push(`${style}  ${icon} ${tc.toolName}(${argStr})\x1b[0m`);
195â†’		}
196â†’		return lines;
197â†’	}
198â†’
199â†’	render(width: number): string[] {
200â†’		const lines: string[] = [];
201â†’
202â†’		for (let i = 0; i < this.messages.length; i++) {
203â†’			const msg = this.messages[i];
204â†’			if (msg.role === "system") {
205â†’				for (const line of msg.content.split("\n")) {
206â†’					lines.push(`\x1b[36m${line}\x1b[0m`);
207â†’				}
208â†’				lines.push("");
209â†’				continue;
210â†’			}
211â†’			const prefix = msg.role === "user" ? "\x1b[1;32mYou:\x1b[0m " : "\x1b[1;36mÊ•â€¢á´¥â€¢Ê”:\x1b[0m ";
212â†’			lines.push(prefix);
213â†’
214â†’			const isLastMessage = i === this.messages.length - 1;
215â†’			if (isLastMessage && this.streaming && this.activeToolCalls.length > 0) {
216â†’				lines.push(...this.renderToolCallLines());
217â†’			}
218â†’
219â†’			const markdown = new Markdown(msg.content, 0, 0, DEFAULT_MARKDOWN_THEME);
220â†’			const rendered = markdown.render(width - 2);
221â†’			lines.push(...rendered);
222â†’			lines.push("");
223â†’		}
224â†’
225â†’		if (this.activeToolCalls.length > 0 && !this.streaming) {
226â†’			lines.push("\x1b[1;36mÊ•â€¢á´¥â€¢Ê”:\x1b[0m ");
227â†’			lines.push(...this.renderToolCallLines());
228â†’			lines.push("");
229â†’		}
230â†’
231â†’		if (this.thinking) {
232â†’			const bear = MessageArea.THINKING_FRAMES[this.frame % MessageArea.THINKING_FRAMES.length];
233â†’			if (this.reasoningText) {
234â†’				const bearPrefix = `${bear} `;
235â†’				const maxLen = width - bearPrefix.length - 1;
236â†’				const display = this.reasoningText.length > maxLen
237â†’					? `â€¦${this.reasoningText.slice(-(maxLen - 1))}`
238â†’					: this.reasoningText;
239â†’				lines.push(`\x1b[1;36m${bear}\x1b[0m \x1b[2m${display}\x1b[0m`);
240â†’			} else {
241â†’				lines.push(`\x1b[1;36m${bear}\x1b[0m `);
242â†’				lines.push("\x1b[2m...\x1b[0m");
243â†’			}
244â†’			lines.push("");
245â†’		}
246â†’
247â†’		return lines;
248â†’	}
249â†’}
250â†’
251â†’class StatusBar implements Component {
252â†’	private status: string = "Ready";
253â†’	private animationInterval: ReturnType<typeof setInterval> | null = null;
254â†’	private frame = 0;
255â†’	private ui: TUI;
256â†’
257â†’	private static readonly THINKING_FRAMES: [string, string][] = [
258â†’		["Ê•â€¢á´¥â€¢Ê”", "?"],
259â†’		["Ê•Â·á´¥Â·Ê”", "??"],
260â†’		["Ê•Ë˜á´¥Ë˜Ê”", "???"],
261â†’		["Ê•-á´¥-Ê”", "..."],
262â†’		["Ê•Ë˜á´¥Ë˜Ê”", ".."],
263â†’		["Ê•â€¢á´¥â€¢Ê”", "!"],
264â†’	];
265â†’	private static readonly THINKING_INTERVAL_MS = 800;
266â†’	private static readonly READY_INTERVAL_MS = 300;
267â†’	private static readonly BLINK_CYCLE = 15;
268â†’	private static readonly SLEEP_START = 100;
269â†’	private static readonly SLEEP_CYCLE = 10;
270â†’
271â†’	constructor(ui: TUI) {
272â†’		this.ui = ui;
273â†’	}
274â†’
275â†’	setStatus(status: string): void {
276â†’		this.stopAnimation();
277â†’		this.status = status;
278â†’		this.frame = 0;
279â†’		if (status === "Thinking...") {
280â†’			this.startAnimation(StatusBar.THINKING_INTERVAL_MS);
281â†’		} else if (status === "Ready") {
282â†’			this.startAnimation(StatusBar.READY_INTERVAL_MS);
283â†’		}
284â†’	}
285â†’
286â†’	private startAnimation(intervalMs: number): void {
287â†’		this.animationInterval = setInterval(() => {
288â†’			this.frame++;
289â†’			this.ui.requestRender();
290â†’		}, intervalMs);
291â†’	}
292â†’
293â†’	private stopAnimation(): void {
294â†’		if (this.animationInterval) {
295â†’			clearInterval(this.animationInterval);
296â†’			this.animationInterval = null;
297â†’		}
298â†’	}
299â†’
300â†’	invalidate(): void {}
301â†’
302â†’	render(width: number): string[] {
303â†’		if (this.status === "Thinking...") {
304â†’			const frameIdx = this.frame % StatusBar.THINKING_FRAMES.length;
305â†’			const [bear, speech] = StatusBar.THINKING_FRAMES[frameIdx];
306â†’			const text = ` ${bear} < ${speech}`;
307â†’			return [`\x1b[7m${text.padEnd(width)}\x1b[0m`];
308â†’		}
309â†’		if (this.status === "Ready") {
310â†’			if (this.frame >= StatusBar.SLEEP_START) {
311â†’				const sleepFrame = (this.frame - StatusBar.SLEEP_START) % StatusBar.SLEEP_CYCLE;
312â†’				const zzz = "z".repeat(Math.min(sleepFrame + 1, 3));
313â†’				const text = ` Ê•-á´¥-Ê” < ${zzz}`;
314â†’				return [`\x1b[7m${text.padEnd(width)}\x1b[0m`];
315â†’			}
316â†’			const isBlink = this.frame % StatusBar.BLINK_CYCLE >= StatusBar.BLINK_CYCLE - 1;
317â†’			const bear = isBlink ? "Ê•-á´¥-Ê”" : "Ê•â€¢á´¥â€¢Ê”";
318â†’			const text = ` ${bear} < Ready`;
319â†’			return [`\x1b[7m${text.padEnd(width)}\x1b[0m`];
320â†’		}
321â†’		const line = `\x1b[7m ${this.status.padEnd(width - 1)}\x1b[0m`;
322â†’		return [line.slice(0, width)];
323â†’	}
324â†’}
