326â†’export class ZiTui {
327â†’	private tui: TUI;
328â†’	private header: Header;
329â†’	private messageArea: MessageArea;
330â†’	private editor: Editor;
331â†’	private statusBar: StatusBar;
332â†’	private container: Container;
333â†’	private agent: Agent;
334â†’	onExit?: () => void;
335â†’
336â†’	constructor(agent: Agent, options: TuiOptions) {
337â†’		this.agent = agent;
338â†’
339â†’		const terminal = new ProcessTerminal();
340â†’		this.tui = new TUI(terminal, true);
341â†’
342â†’		this.header = new Header(options);
343â†’		this.messageArea = new MessageArea(this.tui);
344â†’		this.editor = new Editor(this.tui, DEFAULT_EDITOR_THEME, { paddingX: 1 });
345â†’		this.statusBar = new StatusBar(this.tui);
346â†’
347â†’		this.container = new Container();
348â†’		this.container.addChild(this.header);
349â†’		this.container.addChild(this.messageArea);
350â†’		this.container.addChild(this.editor);
351â†’		this.container.addChild(this.statusBar);
352â†’
353â†’		this.tui.addChild(this.container);
354â†’		this.tui.setFocus(this.editor);
355â†’
356â†’		this.editor.onSubmit = async (text: string) => {
357â†’			await this.handleSubmit(text);
358â†’		};
359â†’
360â†’		this.tui.addInputListener((data: string) => {
361â†’			if (matchesKey(data, "ctrl+c")) {
362â†’				this.onExit?.();
363â†’				return { consume: true };
364â†’			}
365â†’			if (matchesKey(data, "ctrl+d") && this.editor.getText().trim() === "") {
366â†’				this.onExit?.();
367â†’				return { consume: true };
368â†’			}
369â†’			return undefined;
370â†’		});
371â†’	}
372â†’
373â†’	private async handleSubmit(text: string): Promise<void> {
374â†’		if (!text.trim()) return;
375â†’
376â†’		this.messageArea.addMessage({ role: "user", content: text });
377â†’		this.editor.setText("");
378â†’		this.messageArea.startThinking();
379â†’		this.statusBar.setStatus("Thinking...");
380â†’		this.tui.requestRender();
381â†’
382â†’		let streamStarted = false;
383â†’
384â†’		try {
385â†’			const response = await this.agent.prompt(text, undefined, (event: StreamEvent) => {
386â†’				switch (event.type) {
387â†’					case "reasoning":
388â†’						this.messageArea.setReasoning(event.text);
389â†’						this.tui.requestRender();
390â†’						break;
391â†’					case "text":
392â†’						if (!streamStarted) {
393â†’							streamStarted = true;
394â†’							this.messageArea.stopThinking();
395â†’							this.messageArea.clearReasoning();
396â†’							this.messageArea.startStreamingMessage();
397â†’						}
398â†’						this.messageArea.appendToStream(event.text);
399â†’						this.tui.requestRender();
400â†’						break;
401â†’					case "tool-call-start":
402â†’						this.messageArea.stopThinking();
403â†’						this.messageArea.clearReasoning();
404â†’						this.messageArea.addToolCall(event.toolName, event.args);
405â†’						this.tui.requestRender();
406â†’						break;
407â†’					case "tool-call-end":
408â†’						this.messageArea.completeToolCall(event.toolName);
409â†’						this.tui.requestRender();
410â†’						break;
411â†’				}
412â†’			});
413â†’
414â†’			if (!streamStarted) {
415â†’				this.messageArea.stopThinking();
416â†’				this.messageArea.addMessage({ role: "assistant", content: response.content });
417â†’			} else {
418â†’				this.messageArea.finishStreaming();
419â†’			}
420â†’			this.messageArea.clearToolCalls();
421â†’			this.messageArea.clearReasoning();
422â†’			this.statusBar.setStatus("Ready");
423â†’		} catch (error) {
424â†’			this.messageArea.stopThinking();
425â†’			const errorMessage = error instanceof Error ? error.message : String(error);
426â†’			if (streamStarted) {
427â†’				this.messageArea.finishStreaming();
428â†’			}
429â†’			this.messageArea.clearToolCalls();
430â†’			this.messageArea.clearReasoning();
431â†’			this.messageArea.addMessage({ role: "assistant", content: `Error: ${errorMessage}` });
432â†’			this.statusBar.setStatus("Error");
433â†’		}
434â†’
435â†’		this.tui.requestRender();
436â†’	}
437â†’
438â†’	start(): void {
439â†’		this.tui.start();
440â†’		const faces = ["Ê•â€¢á´¥â€¢Ê”", "Ê•Ë˜á´¥Ë˜Ê”", "Ê•Â·á´¥Â·Ê”", "Ê•-á´¥-Ê”"];
441â†’		for (let i = faces.length - 1; i > 0; i--) {
442â†’			const j = Math.floor(Math.random() * (i + 1));
443â†’			[faces[i], faces[j]] = [faces[j], faces[i]];
444â†’		}
445â†’		const W = 24;
446â†’		const logo = [
447â†’			`${faces[0]}â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€${faces[1]}`,
448â†’			`â”‚${"   __  __ _".padEnd(W)}â”‚`,
449â†’			`â”‚${"   \\ \\/ /(_)".padEnd(W)}â”‚`,
450â†’			`â”‚${"    >  < | |".padEnd(W)}â”‚`,
451â†’			`â”‚${`   /_/\\_\\|_|  v${VERSION}`.padEnd(W)}â”‚`,
452â†’			`${faces[2]}â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€${faces[3]}`,
453â†’		].join("\n");
454â†’		this.messageArea.addMessage({ role: "system", content: logo });
455â†’		this.statusBar.setStatus("Ready");
456â†’		this.tui.requestRender();
457â†’	}
458â†’
459â†’	stop(): void {
460â†’		this.statusBar.setStatus("Stopped");
461â†’		this.tui.stop();
462â†’	}
463â†’
464â†’	addMessage(message: MessageItem): void {
465â†’		this.messageArea.addMessage(message);
466â†’		this.tui.requestRender();
467â†’	}
468â†’}
469â†’
470â†’export function createTui(agent: Agent, options: TuiOptions): ZiTui {
471â†’	return new ZiTui(agent, options);
472â†’}
