class MessageArea implements Component {
	private messages: MessageItem[] = [];
	private ui: TUI;
	private thinking = false;
	private frame = 0;
	private animationInterval: ReturnType<typeof setInterval> | null = null;
	private reasoningText = "";
	private activeToolCalls: ToolCallStatus[] = [];

	private static readonly THINKING_FRAMES = ["Ê•â€¢á´¥â€¢Ê”:", "Ê•Â·á´¥Â·Ê”:", "Ê•Ë˜á´¥Ë˜Ê”:", "Ê•-á´¥-Ê”:"];
	private static readonly THINKING_INTERVAL_MS = 800;

	constructor(ui: TUI) {
		this.ui = ui;
	}

	addMessage(message: MessageItem): void {
		this.messages.push(message);
	}

	clear(): void {
		this.messages = [];
	}

	startThinking(): void {
		this.thinking = true;
		this.frame = 0;
		this.animationInterval = setInterval(() => {
			this.frame++;
			this.ui.requestRender();
		}, MessageArea.THINKING_INTERVAL_MS);
	}

	stopThinking(): void {
		this.thinking = false;
		if (this.animationInterval) {
			clearInterval(this.animationInterval);
			this.animationInterval = null;
		}
	}

	setReasoning(text: string): void {
		this.reasoningText += text;
		const lines = this.reasoningText.split("\n");
		this.reasoningText = lines[lines.length - 1];
	}

	clearReasoning(): void {
		this.reasoningText = "";
	}

	addToolCall(toolName: string, args: Record<string, unknown>): void {
		this.activeToolCalls.push({ toolName, args, done: false });
	}

	completeToolCall(toolName: string): void {
		for (let i = this.activeToolCalls.length - 1; i >= 0; i--) {
			if (this.activeToolCalls[i].toolName === toolName && !this.activeToolCalls[i].done) {
				this.activeToolCalls[i].done = true;
				break;
			}
		}
	}

	clearToolCalls(): void {
		this.activeToolCalls = [];
	}

	private streaming = false;

	startStreamingMessage(): void {
		this.messages.push({ role: "assistant", content: "" });
		this.streaming = true;
	}

	appendToStream(chunk: string): void {
		if (!this.streaming || this.messages.length === 0) return;
		const last = this.messages[this.messages.length - 1];
		last.content += chunk;
	}

	finishStreaming(): void {
		this.streaming = false;
	}

	invalidate(): void {}

	private renderToolCallLines(): string[] {
		const lines: string[] = [];
		for (const tc of this.activeToolCalls) {
			const icon = tc.done ? "âœ“" : "âŸ³";
			const style = tc.done ? "\x1b[2m" : "\x1b[33m";
			const argStr = formatToolArgs(tc.args);
			lines.push(`${style}  ${icon} ${tc.toolName}(${argStr})\x1b[0m`);
		}
		return lines;
	}

	render(width: number): string[] {
		const lines: string[] = [];

		for (let i = 0; i < this.messages.length; i++) {
			const msg = this.messages[i];
			if (msg.role === "system") {
				for (const line of msg.content.split("\n")) {
					lines.push(`\x1b[36m${line}\x1b[0m`);
				}
				lines.push("");
				continue;
			}
			const prefix = msg.role === "user" ? "\x1b[1;32mYou:\x1b[0m " : "\x1b[1;36mÊ•â€¢á´¥â€¢Ê”:\x1b[0m ";
			lines.push(prefix);

			const isLastMessage = i === this.messages.length - 1;
			if (isLastMessage && this.streaming && this.activeToolCalls.length > 0) {
				lines.push(...this.renderToolCallLines());
			}

			const markdown = new Markdown(msg.content, 0, 0, DEFAULT_MARKDOWN_THEME);
			const rendered = markdown.render(width - 2);
			lines.push(...rendered);
			lines.push("");
		}

		if (this.activeToolCalls.length > 0 && !this.streaming) {
			lines.push("\x1b[1;36mÊ•â€¢á´¥â€¢Ê”:\x1b[0m ");
			lines.push(...this.renderToolCallLines());
			lines.push("");
		}

		if (this.thinking) {
			const bear = MessageArea.THINKING_FRAMES[this.frame % MessageArea.THINKING_FRAMES.length];
			if (this.reasoningText) {
				const bearPrefix = `${bear} `;
				const maxLen = width - bearPrefix.length - 1;
				const display = this.reasoningText.length > maxLen
					? `â€¦${this.reasoningText.slice(-(maxLen - 1))}`
					: this.reasoningText;
				lines.push(`\x1b[1;36m${bear}\x1b[0m \x1b[2m${display}\x1b[0m`);
			} else {
				lines.push(`\x1b[1;36m${bear}\x1b[0m `);
				lines.push("\x1b[2m...\x1b[0m");
			}
			lines.push("");
		}

		return lines;
	}
}

class StatusBar implements Component {
	private status: string = "Ready";
	private animationInterval: ReturnType<typeof setInterval> | null = null;
	private frame = 0;
	private ui: TUI;

	private static readonly THINKING_FRAMES: [string, string][] = [
		["Ê•â€¢á´¥â€¢Ê”", "?"],
		["Ê•Â·á´¥Â·Ê”", "??"],
		["Ê•Ë˜á´¥Ë˜Ê”", "???"],
		["Ê•-á´¥-Ê”", "..."],
		["Ê•Ë˜á´¥Ë˜Ê”", ".."],
		["Ê•â€¢á´¥â€¢Ê”", "!"],
	];
	private static readonly THINKING_INTERVAL_MS = 800;
	private static readonly READY_INTERVAL_MS = 300;
	private static readonly BLINK_CYCLE = 15;
	private static readonly SLEEP_START = 100;
	private static readonly SLEEP_CYCLE = 10;

	constructor(ui: TUI) {
		this.ui = ui;
	}

	setStatus(status: string): void {
		this.stopAnimation();
		this.status = status;
		this.frame = 0;
		if (status === "Thinking...") {
			this.startAnimation(StatusBar.THINKING_INTERVAL_MS);
		} else if (status === "Ready") {
			this.startAnimation(StatusBar.READY_INTERVAL_MS);
		}
	}

	private startAnimation(intervalMs: number): void {
		this.animationInterval = setInterval(() => {
			this.frame++;
			this.ui.requestRender();
		}, intervalMs);
	}

	private stopAnimation(): void {
		if (this.animationInterval) {
			clearInterval(this.animationInterval);
			this.animationInterval = null;
		}
	}

	invalidate(): void {}

	render(width: number): string[] {
		if (this.status === "Thinking...") {
			const frameIdx = this.frame % StatusBar.THINKING_FRAMES.length;
			const [bear, speech] = StatusBar.THINKING_FRAMES[frameIdx];
			const text = ` ${bear} < ${speech}`;
			return [`\x1b[7m${text.padEnd(width)}\x1b[0m`];
		}
		if (this.status === "Ready") {
			if (this.frame >= StatusBar.SLEEP_START) {
				const sleepFrame = (this.frame - StatusBar.SLEEP_START) % StatusBar.SLEEP_CYCLE;
				const zzz = "z".repeat(Math.min(sleepFrame + 1, 3));
				const text = ` Ê•-á´¥-Ê” < ${zzz}`;
				return [`\x1b[7m${text.padEnd(width)}\x1b[0m`];
			}
			const isBlink = this.frame % StatusBar.BLINK_CYCLE >= StatusBar.BLINK_CYCLE - 1;
			const bear = isBlink ? "Ê•-á´¥-Ê”" : "Ê•â€¢á´¥â€¢Ê”";
			const text = ` ${bear} < Ready`;
			return [`\x1b[7m${text.padEnd(width)}\x1b[0m`];
		}
		const line = `\x1b[7m ${this.status.padEnd(width - 1)}\x1b[0m`;
		return [line.slice(0, width)];
	}
}

export class ZiTui {
	private tui: TUI;
	private header: Header;
	private messageArea: MessageArea;
	private editor: Editor;
	private statusBar: StatusBar;
	private container: Container;
	private agent: Agent;
	onExit?: () => void;

	constructor(agent: Agent, options: TuiOptions) {
		this.agent = agent;

		const terminal = new ProcessTerminal();
		this.tui = new TUI(terminal, true);

		this.header = new Header(options);
		this.messageArea = new MessageArea(this.tui);
		this.editor = new Editor(this.tui, DEFAULT_EDITOR_THEME, { paddingX: 1 });
		this.statusBar = new StatusBar(this.tui);

		this.container = new Container();
		this.container.addChild(this.header);
		this.container.addChild(this.messageArea);
		this.container.addChild(this.editor);
		this.container.addChild(this.statusBar);

		this.tui.addChild(this.container);
		this.tui.setFocus(this.editor);

		this.editor.onSubmit = async (text: string) => {
			await this.handleSubmit(text);
		};

		this.tui.addInputListener((data: string) => {
			if (matchesKey(data, "ctrl+c")) {
				this.onExit?.();
				return { consume: true };
			}
			if (matchesKey(data, "ctrl+d") && this.editor.getText().trim() === "") {
				this.onExit?.();
				return { consume: true };
			}
			return undefined;
		});
	}

	private async handleSubmit(text: string): Promise<void> {
		if (!text.trim()) return;

		this.messageArea.addMessage({ role: "user", content: text });
		this.editor.setText("");
		this.messageArea.startThinking();
		this.statusBar.setStatus("Thinking...");
		this.tui.requestRender();

		let streamStarted = false;

		try {
			const response = await this.agent.prompt(text, undefined, (event: StreamEvent) => {
				switch (event.type) {
					case "reasoning":
						this.messageArea.setReasoning(event.text);
						this.tui.requestRender();
						break;
					case "text":
						if (!streamStarted) {
							streamStarted = true;
							this.messageArea.stopThinking();
							this.messageArea.clearReasoning();
							this.messageArea.startStreamingMessage();
						}
						this.messageArea.appendToStream(event.text);
						this.tui.requestRender();
						break;
					case "tool-call-start":
						this.messageArea.stopThinking();
						this.messageArea.clearReasoning();
						this.messageArea.addToolCall(event.toolName, event.args);
						this.tui.requestRender();
						break;
					case "tool-call-end":
						this.messageArea.completeToolCall(event.toolName);
						this.tui.requestRender();
						break;
				}
			});

			if (!streamStarted) {
				this.messageArea.stopThinking();
				this.messageArea.addMessage({ role: "assistant", content: response.content });
			} else {
				this.messageArea.finishStreaming();
			}
			this.messageArea.clearToolCalls();
			this.messageArea.clearReasoning();
			this.statusBar.setStatus("Ready");
		} catch (error) {
			this.messageArea.stopThinking();
			const errorMessage = error instanceof Error ? error.message : String(error);
			if (streamStarted) {
				this.messageArea.finishStreaming();
			}
			this.messageArea.clearToolCalls();
			this.messageArea.clearReasoning();
			this.messageArea.addMessage({ role: "assistant", content: `Error: ${errorMessage}` });
			this.statusBar.setStatus("Error");
		}

		this.tui.requestRender();
	}

	start(): void {
		this.tui.start();
		const faces = ["Ê•â€¢á´¥â€¢Ê”", "Ê•Ë˜á´¥Ë˜Ê”", "Ê•Â·á´¥Â·Ê”", "Ê•-á´¥-Ê”"];
		for (let i = faces.length - 1; i > 0; i--) {
			const j = Math.floor(Math.random() * (i + 1));
			[faces[i], faces[j]] = [faces[j], faces[i]];
		}
		const W = 24;
		const logo = [
			`${faces[0]}â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€${faces[1]}`,
			`â”‚${"   __  __ _".padEnd(W)}â”‚`,
			`â”‚${"   \\ \\/ /(_)".padEnd(W)}â”‚`,
			`â”‚${"    >  < | |".padEnd(W)}â”‚`,
			`â”‚${`   /_/\\_\\|_|  v${VERSION}`.padEnd(W)}â”‚`,
			`${faces[2]}â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€${faces[3]}`,
		].join("\n");
		this.messageArea.addMessage({ role: "system", content: logo });
		this.statusBar.setStatus("Ready");
		this.tui.requestRender();
	}

	stop(): void {
		this.statusBar.setStatus("Stopped");
		this.tui.stop();
	}

	addMessage(message: MessageItem): void {
		this.messageArea.addMessage(message);
		this.tui.requestRender();
	}
}

export function createTui(agent: Agent, options: TuiOptions): ZiTui {
	return new ZiTui(agent, options);
}

